import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';

var rows = [];

export function getContentData() {
  return new Promise((resolve) => {
    fetch('http://localhost:4000/despesas')
      .then(response => response.json())
      .then(data => resolve(rows = data));
  })
}


export default function BasicTable() {

  React.useEffect(() => {
    getContentData();
  }, []);

  return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 650 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell >Identificador</TableCell>
            <TableCell align="right">Descrição</TableCell>
            <TableCell align="right">Categoria</TableCell>
            <TableCell align="right">Valor</TableCell>
            <TableCell align="right">Data</TableCell>
            <TableCell align="right">Forma Pagamento</TableCell>
            <TableCell align="right">Fornecedor</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow
              key={row.id}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >
              <TableCell component="th" scope="row">
                {row.id}
              </TableCell>
              <TableCell align="right">{row.descricao}</TableCell>
              <TableCell align="right">{row.categoria_id}</TableCell>
              <TableCell align="right">{row.valor}</TableCell>
              <TableCell align="right">{row.data}</TableCell>
              <TableCell align="right">{row.forma_pagamento_id}</TableCell>
              <TableCell align="right">{row.fornecedor_id}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
