import React, { useState } from 'react';
import Button from '@mui/material/Button';
import PaymentsIcon from '@mui/icons-material/Payments';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Grid from '@mui/material/Grid';
import TablePagamentos from './tables/TableFornecedores';
import Input from '@mui/material/Input';


const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  pt: 2,
  px: 4,
  pb: 3,
};

function CadastroModal() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  const handleSave = () => {
    setOpen(false);
  };

  const [name, setValor] = useState('');
  const handleChange = (event) => {
    setValor(event.target.value);
  };

  return (
    <React.Fragment>
      <Button onClick={handleOpen} variant="contained">Cadastro</Button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="child-modal-title"
        aria-describedby="child-modal-description"
      >
        <Box sx={{ ...style, width: 300, height: 200 }}>
          <Grid
          container
          direction="column"
          justifyContent="space-evenly"
          alignItems="center">
            <h2 id="child-modal-title">Cadastro</h2>
            <Input color="primary" value={name} onChange={handleChange} variant="outlined" placeholder="Nome" size="md" />
            <Grid
            sx={{mt:5}}
              container
              direction="row"
              justifyContent="center"
              alignItems="flex-end"
            >
              <Button variant="outlined" onClick={handleSave} sx={{mr: 2}}>Salvar</Button>
              <Button variant="outlined" color="secondary" onClick={handleClose}>Fechar</Button>
            </Grid>
          </Grid>
        </Box>
      </Modal>
    </React.Fragment>
  );
}

export default function ButtonFornecedores() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  return (
    <div>
    <Button variant="contained" onClick={handleOpen} startIcon={<PaymentsIcon />}>Pagamento</Button>
      <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="parent-modal-title"
          aria-describedby="parent-modal-description"
        >
        <Box sx={{ ...style, width: 400, height: 450 }}>
          <Grid container direction="row" justifyContent="space-between" alignItems="center">
            <h2 id="parent-modal-title">Itens</h2>
            <CadastroModal />
          </Grid>
          <TablePagamentos />
          <Grid
          sx={{mt:2}}
            container
            direction="row"
            justifyContent="flex-end"
            alignItems="flex-end"
          >
            <Button variant="outlined" color="secondary" onClick={handleClose}>Fechar</Button>
          </Grid>
        </Box>
      </Modal>
    </div>
  );
}
