import React, { useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import SellIcon from '@mui/icons-material/Sell';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Grid from '@mui/material/Grid';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  pt: 2,
  px: 4,
  pb: 3,
};

var categorias = [];
var fornecedores = [];
var pagamentos = [];

// function getData() {
//   fetch('http://localhost:4000/categorias')
//   .then(response => response.json())
//   .then(data => categorias = data);

// fetch('http://localhost:4000/fornecedores')
//   .then(response => response.json())
//   .then(data => fornecedores = data);

//   fetch('http://localhost:4000/formas-pagto')
//   .then(response => response.json())
//   .then(data => pagamentos = data);
// }

/**
function setNewDespesa() {
  const requestOptions = {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: {
      'descricao': descricao,
      'categoria_id': categoria.value,
      'valor': valor,
      'data': Date.now(),
      'forma_pagamento_id': pgto.value,
      'fornecedor_id': fornecedor.value
    } 
  };
  fetch('http://localhost:4000/despesas/add', requestOptions)
      .then(response => response.json())
      .then(data => this.setState({ postId: data.id }));
}
*/


export default function ButtonDespesa() {
  const [ descricao, setDescricao ] = useState('');
  const [ categoria, setCategoria ] = useState('');
  const [ valor, setValor ] = useState('');
  const [ pgto ] = useState('');
  const [ fornecedor ] = useState('');

  const [ categorias, setCategorias ] = useState(null);
  const [ fornecedores, setFornecedores ] = useState(null);
  const [ pgtos, setPgto ] = useState(null);
  useEffect(
    () => {
      async function fetchProduct() {
        const responseCat = await fetch(`http://localhost:4000/categorias`);
        const dataCat = await responseCat.json();
        setCategorias(dataCat);

        const responseFornecedores = await fetch(`http://localhost:4000/fornecedores`);
        const dataFornecedores = await responseFornecedores.json();
        setFornecedores(dataFornecedores);

        const responsePgto = await fetch(`http://localhost:4000/formas-pagto`);
        const dataPgto = await responsePgto.json();
        setPgto(dataPgto);
      }
      fetchProduct();
    },
  );


  const handleSubmit = async (event) => {
		event.preventDefault();
		const newProduct = { descricao, categoria, valor, pgto, fornecedor };
		console.log(newProduct);
		const post = await fetch('http://localhost:4000/despesas', {
			method: 'POST',
			headers: {
				'Content-Type': 'application/json'
			},
			body: JSON.stringify(newProduct)
		});
		if (post.ok) {
			console.log('Product added successfully');
			// window.location.reload();
      setOpen(false);
		} else {
			console.log('Product not added');
		}
	};

  const [open, setOpen] = React.useState(false);
  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
    <Button variant="contained" color='secondary' onClick={handleOpen} startIcon={<SellIcon />}>Despesas</Button>
      <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="parent-modal-title"
          aria-describedby="parent-modal-description"
        >
        <Box sx={{ ...style, width: 400, height: 450 }}>
          <Grid
          container
          direction="column"
          justifyContent="space-evenly"
          alignItems="center">
            <h2 id="child-modal-title">Cadastro</h2>
            <Input color="primary" value={descricao} onChange={(e) => setDescricao(e.target.value)} variant="outlined" placeholder="Descrição" size="md" />
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Categoria</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={categoria}
                label="Categoria"
                onChange={(e) => setCategoria(e.target.value)}
              >
                {/* {categorias.map((cat) => ( */}
                  {/* <MenuItem >{categorias}</MenuItem> */}
                {/* ))} */}
              </Select>
            </FormControl>
            <Input color="primary" value={valor} onChange={(e) => setValor(e.target.value)} variant="outlined" placeholder="Valor" size="md" />
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Pagamento</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={pgto}
                label="Pagamento"
                onChange={(e) => setPgto(e.target.value)}
              >
                {/* {pgtos.map((pag) => (
                  <MenuItem value={pag.id}>{pag.nome}</MenuItem>
                ))} */}
              </Select>
            </FormControl>
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Fornecedor</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={fornecedor}
                label="Fornecedor"
                onChange={(e) => setFornecedores(e.target.value)}
              >
                {/* {fornecedores.map((forn) => (
                  <MenuItem value={forn.id}>{forn.nome}</MenuItem>
                ))} */}
              </Select>
            </FormControl>
            <Grid
            sx={{mt:5}}
              container
              direction="row"
              justifyContent="center"
              alignItems="flex-end"
            >
              <Button variant="outlined" onClick={handleSubmit} sx={{mr: 2}}>Salvar</Button>
              <Button variant="outlined" color="secondary" onClick={handleClose}>Fechar</Button>
            </Grid>
          </Grid>
        </Box>
      </Modal>
    </div>
  );
}
