import React, { useState } from 'react';
import Button from '@mui/material/Button';
import SellIcon from '@mui/icons-material/Sell';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Grid from '@mui/material/Grid';
import Input from '@mui/material/Input';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  boxShadow: 24,
  pt: 2,
  px: 4,
  pb: 3,
};

export default function ButtonDespesa() {
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };
  const handleSave = () => {
    setOpen(false);
  };
  const [descricao, categoria, setValor, setCategoria] = useState('');
  const handleChange = (event) => {
    setValor(event.target.value);
    setCategoria(event.target.value);
  };
  return (
    <div>
    <Button variant="contained" color='secondary' onClick={handleOpen} startIcon={<SellIcon />}>Despesas</Button>
      <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="parent-modal-title"
          aria-describedby="parent-modal-description"
        >
        <Box sx={{ ...style, width: 400, height: 450 }}>
          <Grid
          container
          direction="column"
          justifyContent="space-evenly"
          alignItems="center">
            <h2 id="child-modal-title">Cadastro</h2>
            <Input color="primary" value={descricao} onChange={handleChange} variant="outlined" placeholder="Descrição" size="md" />
            <FormControl fullWidth>
              <InputLabel id="demo-simple-select-label">Categoria</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={categoria}
                label="Categoria"
                onChange={handleChange}
              >
                <MenuItem value={10}>Ten</MenuItem>
                <MenuItem value={20}>Twenty</MenuItem>
                <MenuItem value={30}>Thirty</MenuItem>
              </Select>
            </FormControl>
            <Input color="primary" value={name} onChange={handleChange} variant="outlined" placeholder="Nome" size="md" />
            <Input color="primary" value={name} onChange={handleChange} variant="outlined" placeholder="Nome" size="md" />
            <Grid
            sx={{mt:5}}
              container
              direction="row"
              justifyContent="center"
              alignItems="flex-end"
            >
              <Button variant="outlined" onClick={handleSave} sx={{mr: 2}}>Salvar</Button>
              <Button variant="outlined" color="secondary" onClick={handleClose}>Fechar</Button>
            </Grid>
          </Grid>
        </Box>
      </Modal>
    </div>
  );
}
